package com.tenpay.injector;/** * log interface for Logger framework. * @author delanding * */public interface ILogger {    /**     * get the start mark for the logger.log;     * */    String getStartMark();    /**     * get the return mark for the logger.ret;     * */    String getReturnMark();    /**     * normal information log for Logger framework     * @param msg the log which will be processed.     * */    void log(String tag, Object msg);    /**     * return information log for Logger framework     * @param className the class of the log function caller.     * @param methodName method name     * @param cost the time of the method cost.     * @param retValue the return value of the method.     * */    void ret(String className, String methodName, long cost, Object retValue);}