package com.tenpay.injector;import android.util.Log;/** * default logger implementation. * * @author delanding */public class LoggerImpl implements ILogger {    private static final String MARK_START = "-> ";    private static final String MARK_RETURN = "<- ";    private static final String TAG_DEF = "DefLogger";    /**     * get the start mark for the logger.log;     */    @Override    public String getStartMark() {        return MARK_START;    }    /**     * get the return mark for the logger.ret;     */    @Override    public String getReturnMark() {        return MARK_RETURN;    }    /**     * normal information log for Logger framework     *     * @param msg the log which will be processed.     */    @Override    public void log(String tag, Object msg) {        Log.i(tag, msg + "");    }    /**     * return information log for Logger framework     *     * @param className  the class of the log function caller.     * @param methodName method name     * @param cost       the time of the method cost.     * @param retValue   the return value of the method.     */    @Override    public void ret(String className, String methodName, long cost, Object retValue) {        String lastMsg = getReturnMark() + String.format("%s[%sms]=%s", methodName, cost + "", retValue + "");        Log.i(TAG_DEF, lastMsg);    }}